INSERT INTO `productsTemplets` VALUES (11,'مكسرات','3','productstemplets/April2020/aunOzYukFUCSnslXHihO.png',NULL,'تُعد المكسرات من بين الأطعمة الصحية التي توفر الكثير من الفوائد الصحية المذهلة، وهي شهية ومتنوعة في الشكل والطعم','4','2020-04-15 15:33:00','2020-04-21 09:02:15'),(15,'زبيب','3','productstemplets/April2020/DHN5V5G0sbmgjnZzOPsS.png',NULL,'للعنب فوائد جمًّة، حيث يحتوي على عناصر غذائية هامة جداً. يحتوي العنب على العديد من الفيتامينات مثل فيتامين سي وفيتامين ب، بالإضافة إلى حمض الفوليك وبعض المعادن مثل البوتاسيوم والنحاس والمنغنيز','4','2020-04-15 15:45:00','2020-04-21 07:50:05'),(16,'كمثري','3','productstemplets/April2020/ZG57736nhitwgZO5cWMd.png',NULL,'1.	تستعمل ثمار الكمثرى لمعالجة ضغط الدم في سن الشيخوخة وتصلب الشرايين وأمراض الكلى وعلاج الاصابات الناتجة عن أمراض القلب والكلى والكبد','5','2020-04-15 15:52:00','2020-04-21 08:59:39'),(17,'الكرز','3','productstemplets/April2020/cew6GQ3rdGXq6QuSGdeV.png',NULL,'هو من الفاكهة التي تحتوي على العديد من الأحماض الأمينية وحمض اليوريك، الجدير بالذكر أن الأطباء ينصحون بتناول الكرز لما به من مكونات تُساهم في التخلص من مرض النقرس والعديد من الأمراض الأخرى','5','2020-04-15 15:54:06','2020-04-15 15:54:06'),(18,'الشمام','3','productstemplets/April2020/f1aOenRg8xQR15n4RdSR.png',NULL,'إن الشمام من الفواكه الصيفية اللذيذة التي تساعد في التغلب على حر الصيف، وله رائحة مميزة وطعم رائع، يتم تناول الشمام على هيئة فاكهة أو عصير، كما أنه قد يدخل في صنع بعض أصناف الحلوى الباردة','5','2020-04-15 15:56:02','2020-04-15 15:56:02'),(19,'جوافة','3','productstemplets/April2020/50zwj94ASUDe8aEpyaWG.png',NULL,'من بين الفواكه التي تتميز بعدة فوائد صحية الجوافة الغنية بفيتامين سي أو (ج) وبنسبة عالية من السكر تبلغ حوالي 17% إلى جانب نسبة ضئيلة من المواد الدهنية والبروتينات، عدا أنها غنية بالأملاح المعدنية مثل الكالسيوم والفوسفور والحديد، وعلى نسبة من فيتامين (أ).','5','2020-04-15 15:58:02','2020-04-15 15:58:02'),(20,'المانجو','3','productstemplets/April2020/9KkHGbrBJOSNIzQqeHtd.png',NULL,'تعتبر المانجو من الفواكه الاستوائية الغنية بالالياف الغذائية مما جعلها تشتهر منذ القدم في تهدئة المعدة وعلاج مشاكل الجهاز الهضمي والامساك، مشاكل القولون العصبي والبواسير.','5','2020-04-15 16:01:21','2020-04-15 16:01:21'),(21,'قشطة','3','productstemplets/April2020/xLGi5WUVUf0IioXeibIc.png',NULL,'تساعد فاكهة القشطة على مكافحة الشيخوخة، والوقاية من السرطان، والحفاظ على ضغط الدم وغيرها من الفوائد الصحية، وذلك لاحتوائها على عناصر غذائية هامة من فيتامينات ومعادن','5','2020-04-15 16:05:03','2020-04-15 16:05:03'),(22,'توت ازرق','3','productstemplets/April2020/J3XmeER1p7MYZ1EV7c6B.png',NULL,'يساعد في الوقاية من أمراض القلب والشرايين.وعلى ضبط مستويات ضغط الدم والوقاية من تصلب الشرايين.والوقاية من السرطانات. وتعزيز وتحسين الذاكرة','4','2020-04-15 16:10:03','2020-04-15 16:10:03'),(23,'.  زنجبيل','3','productstemplets/April2020/iAkZsItqh4fKivWR4Pdz.png',NULL,'من النباتات الاستوائية الذى يتميز بلونه الأخضر وسيقانه ذات الروائح الجذابة فضلا عن ذلك له العديد من الفوائد الصحية للمرضى والحامل والبشرة والشعر، حيث إنه يحتوى على مجموعه كبيرة من مضادات الأكسدة والمعادن والزيوت الطبيعية التى تدخل فى العلاجات ومنها الفسفور والزنك ومضادات الهيستامين.','4','2020-04-15 16:12:29','2020-04-15 16:12:29'),(24,'بطيخ','3','productstemplets/April2020/F6n6l4T0DDrWNICcvITE.png',NULL,'الليكوبين الموجود في البطيخ هو أحد مضادات الأكسدة التي تعمل على الوقاية من السرطان كما يحتوي على البوتاسيوم والمانجنيز وهي عناصر تؤدي إلى خفض الضغط ايضا يؤدي لزيادة إنتاج الطاقة: فهو غني بالماغنيسيوم والبوتاسيوم مما يساهم في زيادة النشاط وترطيب البشرة وزيادة العملية الجنسية','5','2020-04-15 16:15:06','2020-04-15 16:15:06'),(25,'خوخ','3','productstemplets/April2020/F1Yl6pTxVOkI70kzmh45.png',NULL,'يزيد من نسبة الشفاء تحتوي ثمرة واحدة من الخوخ على 15% من فيتامين سي التي يحتاجها الجسم يوميًا، والتي تساعد على التئام الجروح وتقوية المناعة، بالإضافة إلى أنها تقضي على \"الجذور الحرة\" وهي المواد الكيميائية المرتبطة بالسرطان التي يمكن أن تلحق الضرر بخلايا الجسم','5','2020-04-15 16:18:36','2020-04-15 16:18:36'),(26,'بابايا','3','productstemplets/April2020/nOC8FTJH6z8J4JCpeaj5.png',NULL,'فوائد ثمرة البابايا تعد ثمرة البابايا غنية بالفيتامينات C و A و E و كذلك فيتامين ب1 وب6 و البوتاسيوم و الكالسيوم و الحديد وهي من الفواكه الممتازة لإنقاص الوزن، فهي تحتوي على القليل من السعرات الحرارية،','5','2020-04-15 16:28:28','2020-04-15 16:28:28'),(27,'فراولة','3','productstemplets/April2020/I58cn98OkoEnRrYTFdCc.png',NULL,'تحتوي القليل من السعرات الحرارية وتقوي جهازنا المناعي كما تحتوي على مجموعة متنوعة من المواد الغذائية على رأسها فيتامين C. وهي غنية بالمواد المضادة للأكسدة التي تحارب الجذور الحرة وتساعد على منع السرطان','4','2020-04-15 16:33:27','2020-04-15 16:33:27'),(28,'كستناء تركية','3','productstemplets/April2020/tOAYSTQLur0rJpu3jq4a.png',NULL,'فوائد الكستناء الصحية كثيرة نظرا لاحتوائها على الكثير من المركبات الغذائية المفيدة للصحة مثل الألياف و المعادن و الفيتامينات و الكربوهيدرات.','4','2020-04-15 16:37:27','2020-04-15 16:37:27'),(29,'جوز هند','3','productstemplets/April2020/eEjxED0H0SyaoJv2BRFA.png',NULL,'تحتوي فاكهة جوز الهند من كمية عالية من الأملاح المعدنية والطاقة. و يحتوي حليب جوز الهند على نسبة مخفضة من الدهون والسكريات ، ولكنه مرطب للغاية ، مثالي بعد يوم من التمرين أو العمل الشاق','5','2020-04-15 16:40:43','2020-04-15 16:40:43'),(30,'كلمنتينا مستورد','3','productstemplets/April2020/sopkb6hydx97dS2GPsJ8.png',NULL,'كلمنتينا \"المندلينا\" هي جيدة لل قلب بسبب وجود البوتاسيوم المعدنية. استهلاك البوتاسيوم كلمنتينا الغنية مساعدة في تطبيع عدم انتظام ضربات القلب أو عدم انتظام ضربات القلب وتساعد أيضا في الحفاظ على مستويات صحية من ضغط الدم ويمنع ارتفاع ضغط الدم. حتى أنه يقلل من خطر ظروف قاتلة مثل السكتة الدماغية','5','2020-04-15 16:43:53','2020-04-15 16:43:53'),(31,'قريب فروت','3','productstemplets/April2020/edr4FshjTVTeUJmvcFsT.png',NULL,'الجريب فروت غني بالمواد المضادة للأكسدة لذلك له دور فعال جداً في الوقاية من أشعة الشمس الضارة وتقليل ظهور علامات الشيخوخة منها التجاعيد والخطوط حول العينين. كما أنه يساعد في إزالة أثار حب الشباب من البشرة ويعمل على إزالة الشوائب والسموم والخلايا الميتة من الجلد. يمكنك استعمال الجريب فروت للتخلص من البقع الداكنة على ركبك ومرافقك ومن جفاف البشرة حيث يعمل على ترطيب الجلد.','5','2020-04-15 16:47:03','2020-04-15 16:47:03'),(32,'كيوي','3','productstemplets/April2020/hbbQFspMsafcajAhwiJg.png',NULL,'الكيوى من الحمضيات مثل الليمون والبرتقال، ويحتوى على كمية كبيرة من فيتامين C، ووفقا لتقرير صدر مؤخرا تحتوى فاكهة الكيوى على فيتامين C، بما يقرب من ضعف البرتقال والليمون، والكيوى لديه مضادات الأكسدة، الذى يقضى على الالتهاب والسرطان، كما أنه يساعد على تعزيز مناعة الجسم ضد الجراثيم الضارة','5','2020-04-15 16:51:38','2020-04-15 16:51:38'),(33,'الهيليون صغير اخضر','3','productstemplets/April2020/m5OPqom0qw8q7haw2cEg.png',NULL,'يعتبر الهليون عالياً في نسب الألياف والماء عموماً، واللذان يعتبران ضروريان لصحة الجهاز الهضمي والتخفيف من فرص الإصابة بسرطان القولون. كبيراً وهاماً في تنظيم عملية التخلص اليومية من الفضلات والسموم. محتملاً في تنظيم عمل جهاز المناعة والحماية من العدوى','5','2020-04-15 16:56:46','2020-04-15 16:56:46'),(34,'رمان احمر','3','productstemplets/April2020/rMJbF3jq7k6rl63BkYD1.png',NULL,'لرمان هو أحد الفواكه الخريفية لذيذة الطعم المعروفة منذ القدم لدى الكثير من الشعوب ، فيه فوائد جمة  بذور الرمان و قشور الرمان.','5','2020-04-15 16:59:27','2020-04-15 16:59:27'),(35,'الموز','3','productstemplets/April2020/hBdBJsFK4XvGurCIybQ5.png',NULL,'مستورد من الاكوادور الموز من الفواكه الاستوائية المشهورة والمتوفرة في معظم أنحاء العالم. ويعد الموز من أفضل المصادر الطبيعية للطاقة والسكريات البسيطة والمعادن المهمة لجسم الانسان، وعادةً ما يؤكل لوحده أو يتم إدخاله في العصائر لإعطاء القوام السميك للعصير أو يستخدم مع سلطات الفواكة أو في صناعة الحلويات','4','2020-04-15 17:02:05','2020-04-15 17:02:05'),(36,'الافوكادو','3','productstemplets/April2020/GUKqwvs9yTdL2ynqcEng.png',NULL,'مستورد من كينيا فاكهة الافوكادو لها مذاق خاص وفريد من نوعه، ويرتبط تناول الأفوكادو بالعديد من الفوائد الصحية وذلك بسبب احتوائها الألياف الضرورية لحياة صحية وعلى فيتامينات مثل فيتامين أ، ب، سي، والمعادن مثل مغنيسيوم، بوتاسيوم، والنحاس.','5','2020-04-15 17:04:37','2020-04-15 17:04:37'),(37,'تفاح سكري','3','productstemplets/April2020